import React, { useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import { connect, useSelector } from 'react-redux';

import SignUpComponent from '../components/SignUpComponent';

import { register } from '../redux/actions';

/**
 * For register new users
 * @param {props} props
 */
function SignUpView(props) {
  const user = useSelector((state) => state.user);

  useEffect(() => {
    if (user.user) {
      props.history.push('/');
    }
  }, [user, props.history]);

  const onRegister = (username, password, isAdmin) => {
    props.dispatch(register(username, password, isAdmin));
  };

  const onCancel = () => {
    props.history.push('/');
  };

  return <SignUpComponent user={user} onRegister={onRegister} onCancel={onCancel} />;
}

export default withRouter(connect()(SignUpView));
