import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Paper, Button, TextField, Typography} from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  usersignUpRoot: {
    margin: 'auto',
  },
  signUpPaper: {
    width: '500px',
    padding: theme.spacing(2),
  },
  signUpRow: {
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(1),
    '&:last-child': {
      paddingBottom: theme.spacing(0),
    },
    '&:first-child': {
      paddingTop: theme.spacing(0),
    },
  },
  signUpButtons: {
    display: 'flex',
    justifyContent: 'flex-end',
  },
  signUpButton: {
    marginLeft: theme.spacing(1),
  },
}));

/**
 * For register new users
 * @param {props} props
 */
function SignUpComponent(props) {
  const classes = useStyles();

  const [username, setUsername] = React.useState('');
  const [password, setPassword] = React.useState('');
  const [password2, setPassword2] = React.useState('');

  const [registerError, setRegisterError] = React.useState('');

  useEffect(() => {
    if (props.user.error) {
      setRegisterError(props.user.error);
    } else {
      setRegisterError('');
    }
  }, [props.user]);

  const onRegister = (e) => {
    e.preventDefault();
    props.onRegister(username, password, false);
  };

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
    setRegisterError('');
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
    setRegisterError('');
  };

  const onChangePassword2 = (e) => {
    setPassword2(e.target.value);
    setRegisterError('');
  };

  const onBlurPassword = () => {
    if (password !== '' && password2 !== '') {
      if (password !== password2) {
        setRegisterError('Passwords do not match.');
      } else {
        setRegisterError('');
      }
    }
  };

  return (
    <div className={classes.usersignUpRoot}>
      <Paper className={classes.signUpPaper} component="form">
        <div className={classes.signUpRow}>
          <Typography variant="h4" align="center">
            Welcome to Selfix!
          </Typography>
        </div>
        <div className={classes.signUpRow}>
          <TextField label="Username" fullWidth value={username} onChange={onChangeUsername} />
        </div>
        <div className={classes.signUpRow}>
          <TextField label="Password" fullWidth value={password} onChange={onChangePassword} error={registerError !== ''} onBlur={onBlurPassword} type="password" />
        </div>
        <div className={classes.signUpRow}>
          <TextField label="Repeat Password" fullWidth value={password2} onChange={onChangePassword2} error={registerError !== ''} onBlur={onBlurPassword} type="password" />
        </div>
        {registerError !== '' ? (
          <div className={classes.signUpRow}>
            <Typography color="error">{registerError}</Typography>
          </div>
        ) : null}
        <div className={classes.signUpRow + ' ' + classes.signUpButtons}>
          <Button className={classes.signUpButton} onClick={props.onCancel}>
            Cancel
          </Button>
          <Button
            className={classes.signUpButton}
            variant="contained"
            color="primary"
            onClick={onRegister}
            disabled={username === '' || password === '' || password2 === '' || registerError !== '' || password !== password2}
            type="submit"
          >
            Register
          </Button>
        </div>
      </Paper>
    </div>
  );
}

export default SignUpComponent;
